int FindFloor(char *inputs, int size_of_input)
{
    int current_floor = 0;

    for (int i = 0; i < size_of_input; i++) {
        current_floor += GetFloorDirection(inputs[i]);
        FindBasementPosition(current_floor, i);
    }

    return current_floor;
}

int GetFloorDirection(char input)
{
    if (input == '(') {
        return 1;
    } else if (input == ')') {
        return -1;
    } else {
        return 0;
    }
}

void FindBasementPosition(int floor, int position)
{
    static bool b_basement_position_found = false;
    
    if (!b_basement_position_found) {
        if (floor ==  BASEMENT_FLOOR) {
            // c is 0 index, add 1 to position to get an index 1 answer
            printf("Basement floor found at position %i.\n", position + 1);
            b_basement_position_found = true;
        }
    }
}
