int FindFloor(char *input, int size_of_input)
{
    int current_floor = 0;

    for (int i = 0; i < size_of_input; i++) {
        current_floor += GetFloorDirection(input[i]);
    }

    return current_floor;
}

int GetFloorDirection(char input)
{
    if (input == '(') {
        return 1;
    } else if (input == ')') {
        return -1;
    } else {
        return 0;
    }
}
